--  end if;

/*
    for k in (select i.ost                        ,
                     i.nls                    nsc ,
                     i.fio                        ,
                     i.key                        ,
                     i.branch                     ,
                     i.ob22de                 ob22,
                     i.bsd                        ,
                     i.kv                         ,
                     s.nls                    KOTL,
                     trim(substr(s.nms,1,38)) nms
--                   a.nls                    S_RU,
--                   a.kv                         ,
--                   trim(substr(a.nms,1,38)) nms
              from   asvo_immobile i,
--                   accounts      a,
                     accounts      s
              where  i.fl=-10                                               and
                     i.tvbv=p_tvbv                                          and
--                   a.nls=vkrzn(substr(gl.aMFO,1,5),i.bsd||'_30'||gl.aMFO) and
