--    end if;

      begin
        select nls,
               trim(substr(nms,1,38))
        into   KOTL_,
               nms_
        from   accounts
        where  branch=k.branch and
               tip='ODB'       and
               dazs is null    and
               nbs=k.bsd       and
               ob22=k.ob22     and
               kv=k.kv         and
