--    exception when OTHERS then
--      bars_audit.error(p_errmask||'(4): '||sqlerrm||' - '||
--                       dbms_utility.format_error_backtrace);
--      p_code := p_code+1;
--      rollback;
--      goto nofoundi;
--    end;

      update asvo_immobile
      set    fl=0
      where  key=k.key;

      begin
        insert
        into   operw (ref,
                      tag,
                      value)
              values (REF_,
                      'ASVOI',
                      to_char(k.key));
      exception WHEN dup_val_on_index then
        update operw
        set    value=to_char(k.key)
        where  ref=REF_ and
               tag='ASVOI';
      end;

      begin
        insert
        into   operw (ref,
                      tag,
                      value)
              values (REF_,
