    select count(1)
    into   c_
    from   V$OPEN_CURSOR c,
           V$SESSION     s
    where  s.status='ACTIVE' and
           s.sid=c.sid       and
           c.sql_text like 'SELECT 3711 FROM DUAL%';

    if c_=0 then

      select 3711
      into   c_
      from   dual;

      begin
--      for k in (select distinct
--                       u1.table_name
