                                          a.percent                                        ,
                                          clean_string(a.kkname)               kkname      ,
                                          a.ob22                                           ,
                                          to_char(a.date_import,'YYYY-MM-DD')  date_import))).getClobVal()|| --rec
                     (select XmlElement("benef",
                             XmlAgg(
                             XmlElement("Row",
                             XMLFOREST(b.IDB         ,
                                       clean_string(b.CODE)                 CODE        ,
                                       nvl(clean_string(b.FIOB),'ПІБ')      FIOB        ,
                                       b.COUNTRYB                                       ,
                                       clean_string(b.FULLADDRESSB)         FULLADDRESSB,
                                       clean_string(b.ICODB)                ICODB       ,
                                       b.DOCTYPEB                                       ,
                                       clean_string(b.DOCSERIALB)           DOCSERIALB  ,
                                       clean_string(b.DOCNUMBERB)           DOCNUMBERB  ,
                                       clean_string(b.DOCORGB)              DOCORGB     ,
                                       to_char(b.DOCDATEB,'YYYY-MM-DD')     DOCDATEB    ,
                                       to_char(b.CLIENTBDATEB,'YYYY-MM-DD') CLIENTBDATEB,
                                       b.CLIENTSEXB                                     ,
                                       clean_string(b.CLIENTPHONEB)         CLIENTPHONEB)))).getClobVal()
                      from   asvo_compen_benef b
                      where  B.ID_COMPEN=a.id) recbm -- benef
--                   (select XmlElement("motion",
--                           XmlAgg(
--                           XmlElement("Row",
--                           XMLFOREST(m.ID_COMPEN,
--                                     m.IDM      ,
--                                     m.SUMOP    ,
--                                     m.DK       ,
--                                     m.DATL     ,
--                                     m.TYPO     ,
--                                     m.MARK     ,
--                                     m.VER      ,
--                                     m.STAT     ,
--                                     m.OST      ,
--                                     m.ZPR      ,
--                                     m.DATP     ,
--                                     m.PREA     ,
--                                     lpad(int2hex(ascii(m.OI)*decode(substr(m.OI,2,1),null,1,256)+nvl(ascii(substr(m.OI,2,1)),0)),length(m.oi)*2,'0') OI,
--                                     lpad(int2hex(ascii(m.OL)*decode(substr(m.OL,2,1),null,1,256)+nvl(ascii(substr(m.OL,2,1)),0)),length(m.ol)*2,'0') OL)))).getClobVal()
--                    from   asvo_compen_motion m
--                    where  m.id_compen=a.id) recbm -- motion
              FROM   asvo_compen a
              WHERE  a.tvbv=p_tvbv and
                     a.status=0)
    loop
--    bars_audit.info(p_errmask||'(-1): ID = '||to_char(k.id));
      l_date_import := k.date_import;
      begin
        update asvo_compen
        set    status=1
        where  id=k.id;
      exception when others then
        bars_audit.error(p_errmask||'(6):'||chr(13)||chr(10)||
                         replace(sqlerrm,chr(10),chr(13)||chr(10))||
                         ' - '||chr(13)||chr(10)||
                         replace(dbms_utility.format_error_backtrace,chr(10),
                         chr(13)||chr(10))||'---'||chr(13)||chr(10)||
                         'необхідно виконати ВІДКАТ згортання...');
        p_code := p_code+1;
        rollback;
        return;
      end;
--    bars_audit.info(p_errmask||'(A): length(l_clob) = '||length(l_clob));
--    bars_audit.info(p_errmask||'(B): length(k.recbm) = '||length(k.recbm));
      if nvl(length(l_clob),0)+length(k.recbm)<23830 then
        l_clob := l_clob||k.recbm;
      else
        if length(l_clob)<23830 then
          l_recbm.extend;
          l_n := l_n + 1;
          l_recbm(l_n).recbenefmotion := b64e(l_clob);
        else
          l_clobtail := replace(replace(l_clob,'<motion>'),'</motion>');
          l_clob     := '';
          loop
            i := instr(l_clobtail,'</Row>');
            if nvl(i,0)>0 then
              l_clobrow := substr(l_clobtail,1,i+5);
              if nvl(length(l_clob),0)+length(l_clobrow)<23813 then
                l_clobtail := substr(l_clobtail,i+6);
                l_clob     := l_clob||l_clobrow;
              else
                l_clob := '<motion>'||l_clob||'</motion>';
                l_recbm.extend;
                l_n := l_n + 1;
                l_recbm(l_n).recbenefmotion := b64e(l_clob);
                l_clob := '';
              end if;
            else
              exit;
            end if;
          end loop;
          if nvl(length(l_clob),0)>0 then
            l_clob := '<motion>'||l_clob||'</motion>';
            l_recbm.extend;
            l_n := l_n + 1;
            l_recbm(l_n).recbenefmotion := b64e(l_clob);
          end if;
        end if;
        l_clob := k.recbm;
      end if;
    end loop;
    if nvl(length(l_clob),0)>0 then
      if length(l_clob)<23830 then
        l_recbm.extend;
        l_n := l_n + 1;
        l_recbm(l_n).recbenefmotion := b64e(l_clob);
      else
        l_clobtail := replace(replace(l_clob,'<motion>'),'</motion>');
        l_clob     := '';
        loop
          i := instr(l_clobtail,'</Row>');
          if nvl(i,0)>0 then
            l_clobrow := substr(l_clobtail,1,i+5);
            if nvl(length(l_clob),0)+length(l_clobrow)<23813 then
              l_clobtail := substr(l_clobtail,i+6);
              l_clob     := l_clob||l_clobrow;
            else
              l_clob := '<motion>'||l_clob||'</motion>';
              l_recbm.extend;
              l_n := l_n + 1;
              l_recbm(l_n).recbenefmotion := b64e(l_clob);
              l_clob := '';
            end if;
          else
            exit;
          end if;
        end loop;
        if nvl(length(l_clob),0)>0 then
          l_clob := '<motion>'||l_clob||'</motion>';
          l_recbm.extend;
          l_n := l_n + 1;
          l_recbm(l_n).recbenefmotion := b64e(l_clob);
        end if;
      end if;
    end if;

    for i in 1..l_recbm.count
    loop
--    bars_audit.info(p_errmask||'(D): record = '||substr(l_recbm(i).recbenefmotion,1,3000));
      begin
        bars_audit.info('load_comp2ced: service '||l_action||' start');
        wsm_mgr.prepare_request(p_url          => l_url_wapp         ,
                                p_action       => l_action           ,
                                p_http_method  => wsm_mgr.G_HTTP_POST,
                                p_content_type => wsm_mgr.G_CT_JSON  ,
                                p_wallet_path  => l_walett_path      , -- null
                                p_wallet_pwd   => l_walett_pass);      -- null
        wsm_mgr.add_header     (p_name => 'Authorization', p_value => l_Authorization_val);
        wsm_mgr.add_parameter  (p_name => 'record'       , p_value => l_recbm(i).recbenefmotion);
        wsm_mgr.execute_request(g_response);
        l_result := g_response.cdoc; -- если есть ответ - в clob будет
        bars_audit.info('load_comp2ced: service '||l_action||' end - '||substr(l_result,1,3000));
        if (substr(l_result,1,3) in ('400','401','404','500') or
            substr(l_result,1,1)='-') and length(l_result)>3 then
          bars_audit.error(p_errmask||'(1): '||chr(13)||chr(10)||
                           replace(l_result,chr(10),chr(13)||chr(10))||'---'||
                           chr(13)||chr(10)||'необхідно виконати ВІДКАТ згортання...');
          p_code := p_code+1;
          rollback to status1;
          return;
        else
--        bars_audit.info('load_comp2ced: service '||l_action||' test rivne = '||
--                        to_number(l_result)||', p_open = '||p_open);
          begin
            p_open := p_open+to_number(l_result);
          exception when others then
            p_open := p_open+1;
          end;
          dbms_application_info.set_client_info('open='||to_char(p_open)||
                                              ', error='||to_char(p_code));
--        commit;
        end if;
      exception when others then
        bars_audit.error(p_errmask||'(2):'||chr(13)||chr(10)||
                         replace(sqlerrm,chr(10),chr(13)||chr(10))||
                         ' - '||chr(13)||chr(10)||
                         replace(dbms_utility.format_error_backtrace,chr(10),
                         chr(13)||chr(10))||'---'||chr(13)||chr(10)||
                         'необхідно виконати ВІДКАТ згортання...');
        p_code := p_code+1;
        rollback to status1;
        dbms_application_info.set_client_info(' ');
        return;
      end;
    end loop;

--  запуск JOBа по загрузке движения

    bars_audit.info('load_comp2ced: job load_motions start');
    BEGIN
      DBMS_JOB.SUBMIT (job       => Job_                    ,
                       what      => 'migraAS.load_motions('''||p_tvbv||''','''||f_ourmfo||''','''||l_branch||''','''||p_errmask||''');',
                       next_date => sysdate                 ,
                       interval  => null                    ,
                       no_parse  => FALSE);
    exception when others then
      bars_audit.error(p_errmask||'(Jd):'||chr(13)||chr(10)||
                       replace(sqlerrm,chr(10),chr(13)||chr(10))||
                       ' - '||chr(13)||chr(10)||
                       replace(dbms_utility.format_error_backtrace,chr(10),
                       chr(13)||chr(10))||'---'||chr(13)||chr(10)||
                       'необхідно виконати ВІДКАТ згортання...');
      p_code := p_code+1;
      rollback to status1;
      dbms_application_info.set_client_info(' ');
      return;
    END;
    bars_audit.info('load_comp2ced: job load_motions after start');

--  сформировать проводку (или проводки) и сделать commit;

    bars_audit.info('load_comp2ced: selected accounts for wiring');
    for k in (select distinct n.nls                      nls  ,
                              n.kv                            ,
                              -a.ostc                    limit,
                              trim(substr(a.nms,1,38))   nms  ,
                              a.branch                        ,
                              replace(a.ob22,'23','01')  ob22 ,
                              c.okpo
              from   (select  distinct
                              substr(nd,1,instr(nd,'_')-1) nls,
                              kv                              ,
                              tvbv
                      from    asvo_compen
                      where   tvbv=p_tvbv) n,
                     accounts              a,
                     customer              c
              where  n.tvbv=p_tvbv and
                     a.nls=n.nls   and
                     a.kv=n.kv     and
                     c.rnk=a.rnk)
    loop
      bars_audit.info('load_comp2ced: run nls = '||k.NLS);
      if k.limit>0 then
        GL.REF
             (REF_);
        dbms_application_info.set_client_info('create ref = '||to_char(REF_));
        bars_audit.info('load_comp2ced: create ref = '||to_char(REF_));
        begin
--        GL.IN_DOC3
--             (REF_ , TT_    , VOB_   , REF_   , SYSDATE, GL.BDATE, 1,
--              k.KV , k.limit, k.KV   , k.limit, NULL   , GL.BDATE, GL.BDATE,
--              nmsd_, R9910_ , gl.AMFO,
--              k.NMS, k.NLS  , gl.AMFO,
--              'Списання сум компенсаційних вкладів на ЦРКР',
--              NULL, okpod_, k.okpo, NULL, NULL, 0, NULL, null);
          GL.IN_DOC3
               (REF_ , TT_    , VOB_   , REF_   , SYSDATE, GL.BDATE, 1,
                k.KV , k.limit, k.KV   , k.limit, NULL   , GL.BDATE, GL.BDATE,
                nmsd_, R9910_ , gl.AMFO,
                k.NMS, k.NLS  , gl.AMFO,
                'Списання сум компенсаційних коштів переданих ТВБВ ('||
                p_tvbv||') '|| k.branch||' до ЦРКР',
                NULL, okpod_, k.okpo, NULL, NULL, 0, NULL, null);
        exception when OTHERS then
          bars_audit.error(p_errmask||'(9):'||chr(13)||chr(10)||
                           replace(sqlerrm,chr(10),chr(13)||chr(10))||
                           ' - '||chr(13)||chr(10)||
                           replace(dbms_utility.format_error_backtrace,chr(10),
                           chr(13)||chr(10))||'---'||chr(13)||chr(10)||
                           'необхідно виконати ВІДКАТ згортання...');
          p_code := p_code+1;
          rollback;
          dbms_application_info.set_client_info(' ');
          return;
        end;
        bars_audit.info('load_comp2ced: GL.IN_DOC3 ok '||REF_);
        begin
          GL.PAYV
             (0, REF_, GL.BDATE, TT_, 1, k.KV, R9910_, k.limit,
                                         k.KV, k.NLS , k.limit);
          bars_audit.info('load_comp2ced: GL.PAYV ok '||REF_);
        exception when OTHERS then
          bars_audit.error(p_errmask||'(3):'||chr(13)||chr(10)||
                           replace(sqlerrm,chr(10),chr(13)||chr(10))||
                           ' - '||chr(13)||chr(10)||
                           replace(dbms_utility.format_error_backtrace,chr(10),
                           chr(13)||chr(10))||'---'||chr(13)||chr(10)||
                           'необхідно виконати ВІДКАТ згортання...');
          p_code := p_code+1;
          rollback;
          dbms_application_info.set_client_info(' ');
          return;
        end;

        begin
          GL.PAY
             (2, REF_, GL.BDATE);
          bars_audit.info('load_comp2ced: GL.PAY2 ok '||REF_);
        exception when OTHERS then
          bars_audit.error(p_errmask||'(4):'||chr(13)||chr(10)||
                           replace(sqlerrm,chr(10),chr(13)||chr(10))||
                           ' - '||chr(13)||chr(10)||
                           replace(dbms_utility.format_error_backtrace,chr(10),
                           chr(13)||chr(10))||'---'||chr(13)||chr(10)||
                           'необхідно виконати ВІДКАТ згортання...');
          p_code := p_code+1;
          rollback;
          dbms_application_info.set_client_info(' ');
          return;
        end;

        begin
          insert
          into   operw (ref,
                        tag,
                        value)
                values (REF_   ,
                        'TVBV ',
                        p_tvbv); -- доп.реквизит для отката
          bars_audit.info('load_comp2ced: insert into operw ok '||REF_);
        exception when OTHERS then
          bars_audit.error(p_errmask||'(5):'||chr(13)||chr(10)||
                           replace(sqlerrm,chr(10),chr(13)||chr(10))||
                           ' - '||chr(13)||chr(10)||
                           replace(dbms_utility.format_error_backtrace,chr(10),
                           chr(13)||chr(10))||'---'||chr(13)||chr(10)||
                           'необхідно виконати ВІДКАТ згортання...');
          p_code := p_code+1;
          rollback;
          dbms_application_info.set_client_info(' ');
          return;
        end;

--      для проводки в ЦА
--      передать в сервис сумму (limit),
--      nls/kv, branch, p_tvbv, date_import (справочно для записи в доп.реквизиты)
        l_action := 'makewiring';
        begin
--        bars_audit.info('load_comp2ced: nls='        ||k.nls                              );
--        bars_audit.info('load_comp2ced: kv='         ||to_char(k.kv)                      );
--        bars_audit.info('load_comp2ced: branch='     ||k.branch                           );
--        bars_audit.info('load_comp2ced: tvbv='       ||p_tvbv                             );
--        bars_audit.info('load_comp2ced: date_import='||to_char(l_date_import,'YYYY-MM-DD'));
          bars_audit.info('load_comp2ced: service '    ||l_action||' start'                 );
          dbms_application_info.set_client_info('service '||l_action||' for ob22='||k.ob22||' start');
          wsm_mgr.prepare_request(p_url          => l_url_wapp         ,
                                  p_action       => l_action           ,
                                  p_http_method  => wsm_mgr.G_HTTP_POST,
                                  p_content_type => wsm_mgr.g_ct_json  ,
                                  p_wallet_path  => l_walett_path      ,
                                  p_wallet_pwd   => l_walett_pass);
          wsm_mgr.add_header     (p_name => 'Authorization', p_value => l_Authorization_val                );
          wsm_mgr.add_parameter  (p_name => 'summa'        , p_value => to_char(k.limit)                   );
          wsm_mgr.add_parameter  (p_name => 'nls'          , p_value => k.nls                              );
          wsm_mgr.add_parameter  (p_name => 'ob22'         , p_value => k.ob22                             );
          wsm_mgr.add_parameter  (p_name => 'kv'           , p_value => to_char(k.kv)                      );
          wsm_mgr.add_parameter  (p_name => 'branch'       , p_value => k.branch                           );
          wsm_mgr.add_parameter  (p_name => 'tvbv'         , p_value => p_tvbv                             );
          wsm_mgr.add_parameter  (p_name => 'date_import'  , p_value => to_char(l_date_import,'YYYY-MM-DD'));
          wsm_mgr.execute_request(g_response);
          l_result := g_response.cdoc; -- если есть ответ - в clob будет
          bars_audit.info('load_comp2ced: service '||l_action||' end - '||substr(l_result,1,3000));
          if substr(l_result,1,3) in ('400','401','404','500') and length(l_result)>3 then
            bars_audit.error(p_errmask||'(7): '||chr(13)||chr(10)||
                             replace(substr(l_result,1,3000),chr(10),chr(13)||chr(10))||
                             '---'||chr(13)||chr(10)||
                             'необхідно виконати ВІДКАТ згортання...');
            p_code := p_code+1;
            rollback;
            dbms_application_info.set_client_info(' ');
            return;
          end if;
        exception when others then
          bars_audit.error(p_errmask||'(8):'||chr(13)||chr(10)||
                           replace(sqlerrm,chr(10),chr(13)||chr(10))||
                           ' - '||chr(13)||chr(10)||
                           replace(dbms_utility.format_error_backtrace,chr(10),
                           chr(13)||chr(10))||
                           case when instr(sqlerrm,'код [')>0 then
                             '---'||chr(13)||chr(10)||
                             'необхідно виконати ВІДКАТ згортання...'
                           else
                             ''
                           end);
          p_code := p_code+1;
          dbms_application_info.set_client_info(' ');
--        rollback;
--        return;
        end;
      end if;
    end loop;
    dbms_application_info.set_client_info(' ');

--  здесь вроде-бы всё нормально, а если так, то commit;
    commit;
    bars_audit.info('load_comp2ced: end');

  end load_comp2ced;


  procedure get_parameters_web_service(p_url_wapp          out varchar2,
                                       p_Authorization_val out varchar2,
                                       p_timeout           out number,
                                       p_walett_path       out varchar2,
                                       p_walett_pass       out varchar2) is
    l_branch             varchar2(30) := sys_context('bars_context','user_branch');
    l_login              varchar2(30);
    l_pass               varchar2(30);
  begin
    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_URL'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   p_url_wapp;
    exception when others then
      p_url_wapp := GetGlobalOption('CRKR_URL');
    end;
    if p_url_wapp is null then
--    l_url_wapp := 'http://url_undefined';
      p_url_wapp := 'http://10.10.10.101:9081/barsroot/api/crkr/';
    end if;
    if substr(p_url_wapp,-1)<>'/' then
      p_url_wapp := p_url_wapp||'/';
    end if;

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_LOGIN'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_login;
    exception when others then
      l_login := GetGlobalOption('CRKR_LOGIN');
    end;

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_PASS'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   l_pass;
    exception when others then
      l_pass := GetGlobalOption('CRKR_PASS');
    end;


    p_Authorization_val := 'Basic '||utl_raw.cast_to_varchar2(utl_encode.base64_encode(
                           utl_raw.cast_to_raw(l_login||':'||l_pass)));
--  l_Authorization_val := 'Basic YWJzYWRtOnF3ZXJ0eQ==';
--  l_Authorization_val := utl_raw.cast_to_varchar2(utl_encode.base64_encode(
--                         utl_raw.cast_to_raw(GetGlobalOption('CRKR_LOGIN')||':'||
--                         GetGlobalOption('CRKR_PASS'))));

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_WALLET_PATH'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   p_walett_path;
    exception when others then
      p_walett_path := GetGlobalOption('CRKR_WALLET_PATH');
    end;

    begin
      execute immediate 'select branch_attribute_utl.get_attribute_value(
                                p_branch_code    => '''||l_branch||''',
                                p_attribute_code => ''CRKR_WALLET_PASS'',
                                p_raise_expt     => 0,
                                p_parent_lookup  => 1,
                                p_check_exist    => 0,
                                p_def_value      => '''')
                         from   dual'
                         into   p_walett_pass;
    exception when others then
      p_walett_pass := GetGlobalOption('CRKR_WALLET_PASS');
    end;


    begin
      p_timeout := greatest(to_number(GetGlobalOption('CRKR_TIMEOUT'), 60));
    exception
      when others then
        p_timeout := 300;
    end;
  end;


