                j.date_last = sysdate
          where j.status = 0 and j.file_table = cur_j.file_table;
          commit;
          continue;
      end if;

      sql_ := 'select j.nsc, j.id, j.tile, s.mark, s.icod from '||cur_j.file_table||' j, '||
                                          'ASVO_S'||l_kk||l_fff_actual||l_kf||' s '||
               'where  s.nsc=j.nsc   and '||
