               '         j.f=''a''     and '||
               '         (instr(''D'',s.attr)*instr(''Z'',s.attr)+instr(''F'',s.attr)=0 or '||
               '          s.attr is null) and '||
               '         (instr(''FREE'',s.fio||'' '')+instr(''Сторнирование Открытия Счета'',s.fio||'' '')=0 or '||
               '          s.fio is null) ';

      --dbms_output.put_line('sql='||sql_);
