          begin
            select a.id into l_compen_id from asvo_compen a where a.nd = l_nd;
            exception when no_data_found then
              begin
                select e.compen_id, e.status into l_compen_id, l_status_err
                from asvo_update_crkr_err e where e.file_table = cur_j.file_table and e.nd = l_nd;
                case l_status_err
                  when 0 then
                              bars_audit.error(l_errmask       ||'(0): '      ||
