                              ', файл='       ||cur_j.file_table||' nd='||l_nd);
                              l_err   := l_err + 1;
                              l_err_f := l_err_f + 1;
                              continue;
                   when 1 then l_ign := l_ign + 1; continue;
                   when 2 then
                               if l_compen_id is null then
                                 bars_audit.error(l_errmask       ||'(0): '      ||
                                 'Статус в таблиці asvo_update_crkr_err 2, але незаповений compen_id'||
                                 ', файл='       ||cur_j.file_table||' nd='||l_nd);
                                 l_err   := l_err + 1;
                                 l_err_f := l_err_f + 1;
                                 continue;
                               end if;
                end case;
                exception when no_data_found then
                  insert into asvo_update_crkr_err(compen_id,
                                                   file_table,
                                                   nd,
                                                   nsc,
                                                   id,
                                                   icod,
                                                   tile,
                                                   status)
                         values(null,
                                cur_j.file_table,
                                l_nd,
                                l_nsc,
