                           ', файл='       ||cur_j.file_table||' nd='||l_nd||' Проставте в таблиці asvo_update_crkr_err ігнорувати(status=1) або вкажіть вклади вручну(compen_id)');
                  l_err   := l_err + 1;
                  l_err_f := l_err_f + 1;
                  continue;
              end;
          end;

          begin
            insert into asvo_update_crkr_compen(compen_id,
                                                file_table,
                                                nsc,
                                                id,
                                                icod,
                                                tile)
                 values(l_compen_id, cur_j.file_table, l_nsc, l_id, l_icod, l_title);


            l_cnt_r := l_cnt_r + 1;
            exception
              when DUP_VAL_ON_INDEX then null;
