              j.date_last = sysdate
        where j.status = 0 and j.file_table = cur_j.file_table;
        else
          update asvo_update_crkr_ffj j
           set
              j.cnt_adapt = l_cnt_r,
              j.cnt_ignore = l_ign,
              j.info      = 'Існують незнайдені вклади. Проставте в таблиці asvo_update_crkr_err ігнорувати(status=1) або вкажіть вклади вручну(compen_id)',
              j.date_last = sysdate
          where j.status = 0 and j.file_table = cur_j.file_table;
          l_err_f_all := l_err_f_all + l_err_f;
